import React, { useState } from 'react';

import Tasks from './Task';


function InputComp() {
    const [valueDiv, setValueDiv ] = useState(" ");
    const [valueTextarea, setValueTexarea] = useState("ger ");
    const [textareaVisible, setTexareaVisible] = useState(true);
    let result;


    function buttonClickAdd () {
       setValueDiv(<Tasks vl = {valueTextarea} />);
       setTexareaVisible (false);
    }

    function buttonClickDel () {

        setTexareaVisible (false);
     }

    function funcEvent (event) {
       setValueTexarea(event.target.value)
    }


    if (textareaVisible) {
        result = <li>
        <textarea value = {valueTextarea} onChange = {funcEvent} style = {{borderRadius: "10px", border: " 1px solid grey", color: "grey"}}/>
         <div style = {{marginBottom: "10px"}}>
        <button onClick = {buttonClickAdd} style = {{marginRight: "10px", background: "green", opacity: "0.5", borderRadius: "10px"}}>добавить задачу</button>
        <button onClick = {buttonClickDel} style = {{marginRight: "10px", background: "red", opacity: "0.5", borderRadius: "10px"}}>отмена</button>
        </div>         
    </li>  
    } 

     return  <span>
         <div>
                {valueDiv}
         </div>
        <div>
                {result}
        </div>

     </span>





}

export default InputComp;
