import React, { useState } from 'react';

import PInput from './Pinput';


function InputComp() {
    const [valueDiv, setValueDiv ] = useState(" ");
    const [valueTextarea, setValueTexarea] = useState("ger ");

    function buttonClick () {
       setValueDiv(<PInput value = {valueTextarea}/>);
    }

     return  <span>
         <div>
             {valueDiv}
         </div>
        <div>
            <li>
                <textarea value = {valueTextarea} onChange = {(event) => setValueTexarea(event.target.value) } style = {{borderRadius: "10px"}}/>
                 <div style = {{marginBottom: "10px"}}>
                <button onClick = {buttonClick} style = {{marginRight: "10px", background: "green", opacity: "0.5", borderRadius: "10px"}}>добавить задачу</button>
                <button style = {{marginRight: "10px", background: "red", opacity: "0.5", borderRadius: "10px"}}>отмена</button>
                </div>         
            </li>  
        </div>

     </span>





}

export default InputComp;
