}

function getSum(arr) {
	let sum = 0;

	for (const elem of arr) {
		sum += +elem;
	}

	return sum;
}
const result = notes.map((note, index) => {
    return <input
        key={index}
        value={note}
        onChange={event => test(index, event)}
    />;
});

return <div>
    {result}
    {getSum(notes)}
</div>;
}
export default App;
