import './App.css';
import React, { useState } from 'react';

const initDate = {
	year:  2025,
	month: 12,
	day:   31,
}
function App() {
const [ obj, setObj] = useState(initDate);

const day = ((year, month, dayy) => {
   const date =  new Date(year, month, dayy);
   const day =  date.getDay();//   день - числом
   const days = ['вс', 'пн', 'вт', 'ср', 'чт', 'пт', 'сб'];
   return days[day];
})

    const edit = ((date, event) => {
        const copy = Object.assign({}, obj);
        copy[date] = event.target.value;
        setObj(copy);
    } )

	return <div>
        <p>
            {obj.year}/{obj.month}/{obj.day} - {day(obj.year, obj.month, obj.day)}
        </p>
        <input value = {obj.year} onChange = {(event) => edit("year", event)}/>
        <input value = {obj.month} onChange = {(event) => edit("month", event)}/>
        <input value = {obj.day} onChange = {(event) => edit ("day", event)}/>
    </div>

    }


export default App;
