import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';



function App() {

const [value, getValue] = useState(" ");

const handleChange = (event) => getValue(event.target.value)

const conversion = (num) => {
    return num * 80;
}

return <div>
    <input value = {value} onChange = {handleChange}/>
    <p>рублей: {conversion(value)} </p>
</div>

}

export default App; 


