import './App.css';
import React, { useState } from 'react';


function App() {
    const [notes, setNotes] = useState(['a', 'b', 'c', 'd', 'e']);
    const map = notes.map((elem, index) => {
        const copy = Object.assign([], notes);
        copy.splice(index, 1); 

       return  ( <div style = {{display: "flex" }}>
           <li key = {index}>{elem}</li>
           <button onClick = {() => setNotes(copy)}>удалить</button>
           </div>)

    })
    return <div>
        <ul>
             {map}
        </ul>
    </div>

}
export default App;
