import './App.css';
import uuid from "react-uuid";
import React, { useState } from 'react';
import DataInput from './DataInput';

function App() {
    const [valueCurrently, setValueP] = useState(" ");
    const [valueAction, setValueAction] = useState(" ");


    function conclusion() {
        setValueP([<DataInput />, ...valueCurrently]);
    }


    return <div style={{ border: "1px solid grey", color: '#00cccc' }}>
        <div style={{ lineHeight: "2.5", margin: "5px" }}>
            Date {new Date().toLocaleDateString()}
        </div>

        <button onFocus={() => setValueAction("Добавить задачу")} onClick={conclusion} style={{ width: "70px", background: "red", opacity: "0.5", margin: "10px", borderRadius: "10px" }}>
            +
        </button>

        <span>
            {valueAction}
        </span>

        <ul style={{ listStyleType: "none" }}>
            {valueCurrently}
        </ul>
    </div>
}
export default App;
