import React, { useState } from 'react';



function Tasks({ vl }) {

    const [valueTask, setValueTask] = useState(true);
    const [valueButton, setValueButton] = useState(true);
    let task;// задача 
    let buttonDel;// кнопка удаления задачи


    if (valueTask) {
        task = <ul>
            <li>
                <input value={vl} style={{ borderRadius: "10px", backgroundColor: "white", margin: "10px", border: "1px solid grey" }} />
            </li>
        </ul>
    }


    if (valueButton) {
        buttonDel = <button onClick={changesTask} style={{ width: "112px", height: "25px", borderRadius: "20px", opacity: "0.5", margin: "8px" }}>
            удалить задачу
        </button>
    }


    function changesTask() {
        setValueTask(false);
        setValueButton(false);
    }


    return <div style={{ display: "flex" }}>
        {task}
        {buttonDel}
    </div>

}
export default Tasks;
