import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {
  const [count, getCount] = useState (0)

  function increase () {
      getCount (count + 1)
  }

  function reduce () {
      getCount(count - 1)
  }

    return (
    < >
        <p>{count}</p>
        <button onClick = {increase}> +++</button>
        <button onClick = {reduce} >---</button>
     </>   
    )

}

export default App;
