import React, { useState } from 'react';



function PInput({vl}) {

    const [valueTask, setValueTask] = useState(true);
    const [valueButton, setValueButton] = useState(true);
    let task;// задача 
    let buttonDel;// кнопка удаления задачи


    if (valueTask) {
        task = <ul>
                    <li>
                         <input value = {vl}  style = {{ borderRadius: "10px", backgroundColor: "grey", opacity: "0.5", margin: "10px"}}/>
                    </li>
               </ul>
    }


    if (valueButton) {
        buttonDel =  <button onClick = {changesTask} style = {{borderRadius: "30px", opacity: "0.5", marginLeft: "10px"}}>удалить задачу</button>
    }


    function changesTask () {
        setValueTask(false);
        setValueButton(false);

    }


    return <div style = {{display: "flex"}}>
                {task}    
                {buttonDel}
          </div>





}

export default PInput;
