import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {
    const [checked, setChecked] = useState(false);

    function inversely () {
        setChecked(!checked);
    }


    function output (condition1) {

       if (condition1 === true) {
          return ("приветствия");
       } else {
           return ("прощания");
       }
    }

    return <div>
        <input type = "checkbox" checked = {checked} onChange = {inversely} />
        <button onClick = {checked}>пизда-кнопка</button>
        <p>{output(checked)}</p>
    </div>

}

export default App;
