import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {

    const [checked, setChecked] = useState(true);
    const  [initially, setInitially] = useState("видимый");

    // const invisible = (() => setInitially(" no"));

    function invisible () {
        setInitially(" no");
    }
    const  visibility = ((check) => {
           if  (check) {
                 return initially;
           } else {
               return invisible();
           }

    })




   return <div>
       <input type = "checkbox" checked = {checked} onChange = {() => setChecked(!checked)}/>
       <p>{visibility(checked)}</p>
   </div>


}


export default App;
