import './App.css';
import uuid from "react-uuid";

import React, { useState } from 'react';

const initProds = [
	{
		id: uuid(),
		name: 'prod1',
		cost: 'cost1',
		desc: 'long description 1',
		comm: 'my super comment 1'
	},
	{
		id: uuid(),
		name: 'prod2',
		cost: 'cost2',
		desc: 'long description 2',
		comm: 'my super comment 2'
	},
	{
		id: uuid(),
		name: 'prod3',
		cost: 'cost3',
		desc: 'long description 3',
		comm: 'my super comment 3'
	},
];

function App() {
    const [notes, setNotes] = useState(initProds);





    const result = notes.map((elem) => {
        let floating = false;


        return <li>
            {elem.name}
            {elem.cost}
            {floating? "elem.desc": "!!"}


            <button onClick = {() => setNotes(!floating)}>описание</button>
            <button>отзыв</button>
        </li>
    })

    return <div>
        <ul>
               {result} 
        </ul>

    </div>


}

export default App;
