import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {

    let today = new Date();
  const [value1, setValue1] = useState(today);
  const [value2, setValue2] = useState(today);
  const [result, setResult] = useState("0");

  const handleChange1 = (event) => setValue1(event.target.value);
  const handleChange2 = (event) => setValue2(event.target.value);

  const differenceDay = (num1, num2) => {
      let arr1 = String(num1).split("-");// ["2025", "12", "31"]
      let arr2 = String(num2).split("-");
      let day = Number(arr1[2]) - Number(arr2[2]);
      let month = Number(arr1[1]) - Number(arr2[1]);
      let monthInYear = month * 30;
      let year = Number(arr1[0]) - Number(arr2[0]);
      let dayInYear = year * 365;
      let result = day + monthInYear + dayInYear;  
      return result;

  }

  return <div>
      <input value = {value1} onChange = {handleChange1}/>
      <input value = {value2} onChange = {handleChange2}/>
      <button onClick = {() => setResult(differenceDay(value1, value2))}>разница </button>
      <p>разница между датами: {result}</p>
  </div>

}

export default App;
