import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {

  const arr = []; 
  for (let i = 0; i <= 70; i++) {
        arr.push(i);
    }
  const map = arr.map((elem, index) => {
      return <option  value = {index} >{elem} лет</option>
  })

  const [value, setValue] = useState('');
  const filter = ((num) => {
      if (num >= 0 && num <= 12) {
          return "от 0 до 12 лет"
      } else if (num >= 13 && num <= 17) {
          return "от 13 до 17 лет"
      } else if (num >= 18 && num <= 25) {
          return "от 18 до 25 лет"
      }
      else {
          return "старше 25 лет"
      }
  })

    return <div>
        <select value={value} onChange={event => setValue(event.target.value)}>
			{map}
		</select>
        <p>{filter(value)}</p>
        <p>{(value >= "0" && value <= "12") && "от 0 до 12 лет"}</p>
        <p>{(value >= "13" && value <= "17") && "от 13 до 17 лет"}</p>
        <p>{(value >= "18" && value <= "25") && "от 18 до 25 лет"}</p>
        <p>{(value > "26") && "старше 25 лет"}</p>

    </div>

}


export default App;
