import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {
    const [checked, setChecked] = useState(true);
    const [initially, setInitially ] = useState(" ")

    function inversely () {
        setChecked(!checked);
    }

    function output (condition) {
       if (condition === true) {
          return ("приветствия");
       } else {
           return ("прощания");
       }
    }

    function reverse () {
        setInitially(output(checked));
    }


    return <div>
        <input type = "checkbox" checked = {checked} onChange = {inversely} />
        <button onClick = {reverse}>пизда-кнопка</button>
        <p>{initially}</p>
    </div>

}

export default App;
