import './App.css';
import React, { useState } from 'react';


function App() {
    const [notes, setNotes] = useState([9, 6, 3]);


    const map = notes.map((elem) => {
       return <input value = {elem} onChange = {(event) => setNotes(event.target.value)}/>
    })

   function sum (arr) {
       let result = 0;

       for ( let elem of arr) {
           result += elem;
       }
       return result;


   }



   return <div>
       {map}
       <div>{sum(notes)}</div>
   </div>
}
export default App;
