import './App.css';
import uuid from 'react-uuid'
import React, { useState } from 'react';

function App() {

 const [value, setValue] = useState (" ");
 const Change = (event) => setValue(event.target.value);
 const [ result, setResult] = useState("");


 const amount = (num) => {
    let arr = String(num).split("");
    let sum = 0;
    for ( let elem of arr) {
        sum += Number(elem)
    }
    return sum;
 }

 return <div>
     <input value = {value} onChange = {Change} onBlur = {() => setResult(amount(value))}/>
     <p>{result}</p>
 </div>

}

export default App;
