import './App.css';
import React, { useState } from 'react';


function App() {
const [notes, setNotes] = useState([1, 2, 3, 4, 5, 6, 7, 8, 9]);
 const changeHandler = ((index, event) => {
    setNotes([...notes.slice(0, index), event.target.value, ...notes.slice(index + 1)]);
})

const average = ((arr) => {
    let sum = 0;
    for (let elem of arr) {

        let result = (sum += elem)/arr.length;
        return result;



    }
})



const map = notes.map((elem, index) => {
    return <input key = {index} value = {elem} onChange = {(event) => changeHandler(index, event)}/>
})

return <div>
   {map}
   <p>{average(notes)}</p>
</div>

}
export default App;
